
// howler.mmo.min.js - Custom Howler Build with MMO audio extension
var Howler = window.Howler || {};
var soundLibrary = {};

function loadSound(key, src) {
    soundLibrary[key] = new Audio(src);
}

function playSound(key) {
    if (soundLibrary[key]) {
        soundLibrary[key].play();
    } else {
        console.warn("Sound", key, "not loaded.");
    }
}

Howler.MMOSound = {
    load: loadSound,
    play: playSound
};
