
// phaser.mmo.min.js - Custom Phaser Build with MMO Placeholder
var Phaser = window.Phaser || {};
Phaser.MMOModule = {
    connect: function(server) {
        console.log("Connecting to MMO server at " + server + " (simulated)");
    },
    send: function(data) {
        console.log("Sending data to MMO server:", data);
    },
    receive: function(callback) {
        console.log("Listening to server (simulated)");
        setTimeout(() => callback({ type: "welcome", playerId: "1234" }), 1000);
    }
};
